function getRandomInt(b,a){return Math.floor(Math.random()*(a-b+1)+b)}function calcVolume(a){return a/100}function ifMp4(){if(navigator.userAgent.match(/Trident/i)){return true}else{if(navigator.userAgent.match(/Safari/i)){return true}else{return false}}}$(document).ready(function(){$(".player").click(function(){var b=$(this).parent().children(".player-title").text();var a=null;switch(b){case"Rain":a="rain";break;case"Campfire":a="fire";break;case"Guitar":a="guitar";break;case"Train travel":a="guitar";break;case"Coffee shop":a="guitar";break;case"Waves":a="fire";break;case"Bon fire":a="fire";break}path="sounds/"+a;var d="#player-"+a;if($(d).hasClass("player-inactive")){if(ifMp4()){$(d).attr("src",path+"/aac/"+a+"1.m4a")}else{$(d).attr("src",path+"/ogg/"+a+"1.ogg")}var c=$(d).get();c[0].play();$(d).removeClass("player-inactive");$(d).addClass("player-active");$(this).html("&#xf04c;")}else{var c=$(d).get();c[0].pause();$(d).removeClass("player-active");$(d).addClass("player-inactive");$(this).html("&#xf04b;")}});$("#player-guitar").bind("ended",function(){if(ifMp4()){$("#player-guitar").attr("src","sounds/guitar/aac/guitar"+getRandomInt(1,11)+".m4a")}else{$("#player-guitar").attr("src","sounds/guitar/ogg/guitar"+getRandomInt(1,11)+".ogg")}var a=$("#player-guitar").get();a[0].play()});$("#slider-rain").change(function(c){var a=$("#slider-rain").get();var b=$("#player-rain").get();b[0].volume=calcVolume(parseInt(a[0].value))});$("#slider-fire").change(function(c){var a=$("#slider-fire").get();var b=$("#player-fire").get();b[0].volume=calcVolume(parseInt(a[0].value))});$("#slider-guitar").change(function(c){var a=$("#slider-guitar").get();var b=$("#player-guitar").get();b[0].volume=calcVolume(parseInt(a[0].value))})});